mapper.select:
select count(*) from tb_buildings
WHERE
id>0


	AND username LIKE #{filter.username}




	AND nickname LIKE #{filter.nickname}



	AND state = #{filter.state}




	AND email LIKE #{filter.email}

SELECT

FROM
tb_buildings
WHERE
id>0


	AND buildings_name LIKE #{filter.buildings_name}




	AND developer_company LIKE #{filter.developer_company}


ORDER BY id DESC

	LIMIT #{filter.first},#{filter.last}

SELECT b.buildings_name,b.server_phone_num,a.*,c.resource_path,c.resource_name,c.resource_alias FROM tb_building_active a LEFT JOIN tb_buildings b ON a.buildings_id=b.id LEFT JOIN tb_building_resource c ON c.big_type=1 AND c.sm_type=1 AND c.pid=b.id WHERE a.id>0
AND a.start_date <=now() AND a.end_date >=now()
AND date_format(a.start_date,'%Y-%m-%d')=#{filter.startdata}
AND b.city_id=#{filter.cityId}
AND b.status=0 ORDER BY a.end_date desc
LIMIT #{filter.first},#{filter.last}
